#pragma config(Motor,  port2,           driveRight,    tmotorVex393_MC29, openLoop)
#pragma config(Motor,  port3,           driveLeft,     tmotorVex393_MC29, openLoop)
#pragma config(Motor,  port4,           piston,        tmotorVex393_MC29, openLoop)
#pragma config(Motor,  port5,           elevator,      tmotorVex393_MC29, openLoop)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

//Globals.
//Damn I wish ROBOTC had encapsulation
#define TRUE 1
#define FALSE 0
#define MAX_SPEED 127
#define MIN_SPEED 1
#define NO_SPEED 0
bool piston_enabled = false;

void singleJoystick() { //Yeah, this is arcade drive
	motor[driveLeft] = (vexRT[Ch2] + vexRT[Ch1]) / 2;
	motor[driveRight] = (vexRT[Ch2] - vexRT[Ch1]) / 2;
}

void dualJoystick() {
	motor[driveLeft] = vexRT[Ch2];
	motor[driveRight] = vexRT[Ch4];
}

void mapJoystick() {

	singleJoystick();

	//Lift/Partner is controller 2
	motor[elevator] = vexRT[Ch1Xmtr2];

	if(vexRT[Btn5DXmtr2] == TRUE || piston_enabled) {
		motor[piston] = MAX_SPEED;
		piston_enabled = !piston_enabled;
		} else if(!piston_enabled) {
		motor[piston] = NO_SPEED;
	}
}


