/*---------------------------------------------------------------------------*/
/*                                                                           */
/*                              Autonomous Task                              */
/*                                                                           */
/*  This task is used to control your robot during the autonomous phase of   */
/*  a VEX Competition.                                                       */
/*	You must modify the code to add your own robot specific commands here.   */
/*---------------------------------------------------------------------------*/

void stopRobot()
{
	motor[bLeft] = 0;
	motor[bRight] = 0;
	motor[fLeft] = 0;
	motor[fRight] = 0;
}

void moveForward(int speed, int durationMilliseconds)
{
	motor[bLeft] = speed;
	motor[bRight] = speed;
	motor[fLeft] = speed;
	motor[fRight] = speed;
	wait1Msec(durationMilliseconds);
	stopRobot();
}

void moveBackward(int speed, int durationMilliseconds)
{
	motor[bLeft] = -1 * speed;
	motor[bRight] = -1 * speed;
	motor[fLeft] = -1 * speed;
	motor[fRight] = -1 * speed;
	wait1Msec(durationMilliseconds);
	stopRobot();
}

void turnLeft(int speed, int durationMilliseconds)
{
	motor[bLeft] = -1 * speed;
	motor[bRight] = speed;
	motor[fLeft] = -1 * speed;
	motor[fRight] = speed;
	wait1Msec(durationMilliseconds);
	stopRobot();
}

void turnRight(int speed, int durationMilliseconds)
{
	motor[bLeft] = speed;
	motor[bRight] = -1 * speed;
	motor[fLeft] = speed;
	motor[fRight] = -1 * speed;
	wait1Msec(durationMilliseconds);
	stopRobot();
}

