/*---------------------------------------------------------------------------*/
/*                                                                           */
/*                              User Control Task                            */
/*                                                                           */
/*  This task is used to control your robot during the user control phase of */
/*  a VEX Competition.                                                       */
/*                                                                           */
/*  You must modify the code to add your own robot specific commands here.   */
/*---------------------------------------------------------------------------*/

task usercontrol()
{

  while (true)
  {
	//Joystick Control
		motor[fLeft] = vexRT[Ch3];
		motor[fRight] = vexRT[Ch2];
		motor[bLeft] = vexRT[Ch3];
		motor[bRight] = vexRT[Ch2];


/*
		//6U - Launcher
 		if (vexRT[Btn6U] == 1) {
		  motor[launcher] = -127;
  	}
		else {
			motor[launcher] = 0;
		}
*/

	//5U & 5D - Flip Disk
	  if(vexRT[Btn5U] == 1) {
			motor[fDisk] = 100;}
		else if (vexRT[Btn5D]) {
			motor[fDisk] = -100;}
    else {
			motor[fDisk] = 0;
		}

	//Lift
		if(vexRT[Btn6U] == 1) {
			motor[lift] = 100;}
		else if (vexRT[Btn6D]) {
			motor[lift] = -100;}
    else {
			motor[lift] = 0;
		}

	}
}
